/**
 * ServiceImpl object for domain model class Themedetail.
 *
 * @see Themedetail
 */
@Service("BrightBankDB.ThemedetailService")
@Validated
@EntityService(entityClass = Themedetail.class, serviceId = "BrightBankDB")
public class ThemedetailServiceImpl implements ThemedetailService {

    private static final Logger LOGGER = LoggerFactory.getLogger(ThemedetailServiceImpl.class);


    @Autowired
    @Qualifier("BrightBankDB.ThemedetailDao")
    private WMGenericDao<Themedetail, Integer> wmGenericDao;

    @Autowired
    @Qualifier("wmAppObjectMapper")
    private ObjectMapper objectMapper;


    public void setWMGenericDao(WMGenericDao<Themedetail, Integer> wmGenericDao) {
        this.wmGenericDao = wmGenericDao;
    }

    @Transactional(value = "BrightBankDBTransactionManager")
    @Override
    public Themedetail create(Themedetail themedetail) {
        LOGGER.debug("Creating a new Themedetail with information: {}", themedetail);

        Themedetail themedetailCreated = this.wmGenericDao.create(themedetail);
        // reloading object from database to get database defined & server defined values.
        return this.wmGenericDao.refresh(themedetailCreated);
    }

    @Transactional(readOnly = true, value = "BrightBankDBTransactionManager")
    @Override
    public Themedetail getById(Integer themedetailId) {
        LOGGER.debug("Finding Themedetail by id: {}", themedetailId);
        return this.wmGenericDao.findById(themedetailId);
    }

    @Transactional(readOnly = true, value = "BrightBankDBTransactionManager")
    @Override
    public Themedetail findById(Integer themedetailId) {
        LOGGER.debug("Finding Themedetail by id: {}", themedetailId);
        try {
            return this.wmGenericDao.findById(themedetailId);
        } catch (EntityNotFoundException ex) {
            LOGGER.debug("No Themedetail found with id: {}", themedetailId, ex);
            return null;
        }
    }

    @Transactional(readOnly = true, value = "BrightBankDBTransactionManager")
    @Override
    public List<Themedetail> findByMultipleIds(List<Integer> themedetailIds, boolean orderedReturn) {
        LOGGER.debug("Finding Themedetails by ids: {}", themedetailIds);

        return this.wmGenericDao.findByMultipleIds(themedetailIds, orderedReturn);
    }


    @Transactional(rollbackFor = EntityNotFoundException.class, value = "BrightBankDBTransactionManager")
    @Override
    public Themedetail update(Themedetail themedetail) {
        LOGGER.debug("Updating Themedetail with information: {}", themedetail);

        this.wmGenericDao.update(themedetail);
        this.wmGenericDao.refresh(themedetail);

        return themedetail;
    }

    @Transactional(value = "BrightBankDBTransactionManager")
    @Override
    public Themedetail partialUpdate(Integer themedetailId, Map<String, Object>themedetailPatch) {
        LOGGER.debug("Partially Updating the Themedetail with id: {}", themedetailId);

        Themedetail themedetail = getById(themedetailId);

        try {
            ObjectReader themedetailReader = this.objectMapper.reader().forType(Themedetail.class).withValueToUpdate(themedetail);
            themedetail = themedetailReader.readValue(this.objectMapper.writeValueAsString(themedetailPatch));
        } catch (IOException ex) {
            LOGGER.debug("There was a problem in applying the patch: {}", themedetailPatch, ex);
            throw new InvalidInputException("Could not apply patch",ex);
        }

        themedetail = update(themedetail);

        return themedetail;
    }

    @Transactional(value = "BrightBankDBTransactionManager")
    @Override
    public Themedetail delete(Integer themedetailId) {
        LOGGER.debug("Deleting Themedetail with id: {}", themedetailId);
        Themedetail deleted = this.wmGenericDao.findById(themedetailId);
        if (deleted == null) {
            LOGGER.debug("No Themedetail found with id: {}", themedetailId);
            throw new EntityNotFoundException(MessageResource.create("com.wavemaker.runtime.entity.not.found"), Themedetail.class.getSimpleName(), themedetailId);
        }
        this.wmGenericDao.delete(deleted);
        return deleted;
    }

    @Transactional(value = "BrightBankDBTransactionManager")
    @Override
    public void delete(Themedetail themedetail) {
        LOGGER.debug("Deleting Themedetail with {}", themedetail);
        this.wmGenericDao.delete(themedetail);
    }

    @Transactional(readOnly = true, value = "BrightBankDBTransactionManager")
    @Override
    public Page<Themedetail> findAll(QueryFilter[] queryFilters, Pageable pageable) {
        LOGGER.debug("Finding all Themedetails");
        return this.wmGenericDao.search(queryFilters, pageable);
    }

    @Transactional(readOnly = true, value = "BrightBankDBTransactionManager")
    @Override
    public Page<Themedetail> findAll(String query, Pageable pageable) {
        LOGGER.debug("Finding all Themedetails");
        return this.wmGenericDao.searchByQuery(query, pageable);
    }

    @Transactional(readOnly = true, value = "BrightBankDBTransactionManager", timeout = 300)
    @Override
    public Downloadable export(ExportType exportType, String query, Pageable pageable) {
        LOGGER.debug("exporting data in the service BrightBankDB for table Themedetail to {} format", exportType);
        return this.wmGenericDao.export(exportType, query, pageable);
    }

    @Transactional(readOnly = true, value = "BrightBankDBTransactionManager", timeout = 300)
    @Override
    public void export(DataExportOptions options, Pageable pageable, OutputStream outputStream) {
        LOGGER.debug("exporting data in the service BrightBankDB for table Themedetail to {} format", options.getExportType());
        this.wmGenericDao.export(options, pageable, outputStream);
    }

    @Transactional(readOnly = true, value = "BrightBankDBTransactionManager")
    @Override
    public long count(String query) {
        return this.wmGenericDao.count(query);
    }

    @Transactional(readOnly = true, value = "BrightBankDBTransactionManager")
    @Override
    public Page<Map<String, Object>> getAggregatedValues(AggregationInfo aggregationInfo, Pageable pageable) {
        return this.wmGenericDao.getAggregatedValues(aggregationInfo, pageable);
    }



}
