    @Transactional(rollbackFor = EntityNotFoundException.class, value = "BrightBankDBTransactionManager")
    @Override
    public WealthGoals update(WealthGoals wealthGoals) {
        LOGGER.debug("Updating WealthGoals with information: {}", wealthGoals);

        this.wmGenericDao.update(wealthGoals);
        this.wmGenericDao.refresh(wealthGoals);

        return wealthGoals;
    }

    @Transactional(value = "BrightBankDBTransactionManager")
    @Override
    public WealthGoals partialUpdate(Integer wealthgoalsId, Map<String, Object>wealthGoalsPatch) {
        LOGGER.debug("Partially Updating the WealthGoals with id: {}", wealthgoalsId);

        WealthGoals wealthGoals = getById(wealthgoalsId);

        try {
            ObjectReader wealthGoalsReader = this.objectMapper.reader().forType(WealthGoals.class).withValueToUpdate(wealthGoals);
            wealthGoals = wealthGoalsReader.readValue(this.objectMapper.writeValueAsString(wealthGoalsPatch));
        } catch (IOException ex) {
            LOGGER.debug("There was a problem in applying the patch: {}", wealthGoalsPatch, ex);
            throw new InvalidInputException("Could not apply patch",ex);
        }

        wealthGoals = update(wealthGoals);

        return wealthGoals;
    }

